INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (1, N'furniture_leather_sofa', N'Comfy Leather Sofa', NULL, CAST(75.00 AS Decimal(18, 2)), CAST(5000.00 AS Decimal(18, 2)), 600, NULL, CAST(60.00 AS Decimal(18, 2)), N'A comfortable leather sofa, perfect for taking quick powernaps.  This magical essence that comes from the volunteer who is the couch transfers some of their energy to the user, giving them a bit more energy to call down and use as they desire.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (2, N'furniture_potted_willflower_plant_small', N'Small Potted Willflower Plant', NULL, CAST(0.00 AS Decimal(18, 2)), CAST(1000.00 AS Decimal(18, 2)), 300, N'item_consumeable_willflower_fresh', CAST(45.00 AS Decimal(18, 2)), N'A small ceramic pot filled with fertile soil.  A willflower plant grows out of it, growing fresh flower petals for covenant members to pluck off (painlessly to the transformee, of course) and take with them.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (4, N'furniture_potted_spellweaver_plant_small', N'Small Potted Spellweaver Plant', NULL, CAST(0.00 AS Decimal(18, 2)), CAST(1000.00 AS Decimal(18, 2)), 300, N'item_consumeable_spellweaver_fresh', CAST(45.00 AS Decimal(18, 2)), N'A small ceramic pot filled with fertile soil.  A spellweaver plant grows out of it, growing fresh flower petals for covenant members to pluck off (painlessly to the transformee, of course) and take with them.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (5, N'furniture_ottoman_rug', N'Intricate Ottoman Rug', NULL, CAST(5.00 AS Decimal(18, 2)), CAST(2500.00 AS Decimal(18, 2)), 400, NULL, CAST(7.50 AS Decimal(18, 2)), N'An intricately woven Ottoman rug, bright and colorful with enough detail to mesmerize.  The consciousness within desired nothing more than to be walked upon and adored (though it may have some objections to slime girls who don''t clean up after themselves with a good steam cleaning.)  Treading on this rug with bare feet will transfer some of its energy into mages standing on it.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (6, N'furniture_globe', N'Mounted Spinning Globe', NULL, CAST(0.00 AS Decimal(18, 2)), CAST(8000.00 AS Decimal(18, 2)), 1200, N'item_consumeable_teleportation_scroll', CAST(360.00 AS Decimal(18, 2)), N'A large globe three feet in diameter, mounted on a fine wooden frame, able to be spun around on its axis.  The consciousness inhabiting the globe was quite the cartographer and geography enthusiast, as their knowledge of all things longtidudinal and latitudinal will slowly manifest itself as teleportation scrolls for your covenant.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (7, N'furniture_wall_painting_female', N'Artistic Female Nude Painting', NULL, CAST(7.00 AS Decimal(18, 2)), CAST(1000.00 AS Decimal(18, 2)), 250, NULL, CAST(22.50 AS Decimal(18, 2)), N'This transformee might not be walking on any catwalks or in fashion shows--or walking at all, really, as they are now two dimensional, trapped inside a painting carefully set in a wooden  canvas.  The transformee is in the shape of a woman holding up an apple in a thoughtful pose while resting naked at a riverbank.  Gazing at this idealistic yet realistically proportioned woman gives viewers some confidence and energy to face the world without hiding their own skin.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (9, N'furniture_cookiejar', N'Ceramic Cookie Jar', NULL, CAST(0.00 AS Decimal(18, 2)), CAST(3000.00 AS Decimal(18, 2)), 500, N'item_consumeable_concealment_cookie', CAST(42.50 AS Decimal(18, 2)), N'The consciousness of a true cookie lover is held inside this blue ceramic cookie jar.  The transformee''s taste for the treat has manifested itself even in their new heavy round body and about every hour and a half a tasty new cookie is materialized out of pure mana.  Whenever someone approaches the jar vibrates a bit and the lid slides a bit open, hoping that someone or something that still has a fingers and a mouth will appreciate its gift.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (10, N'furniture_desk_lamp', N'Swiveling Desk Lamp', NULL, CAST(20.00 AS Decimal(18, 2)), CAST(2200.00 AS Decimal(18, 2)), 400, NULL, CAST(32.50 AS Decimal(18, 2)), N'The consciousness of the transformee inside this swiveling desk lamp would like nothing more than to help shine some light on those in need, whether it be helping a tired young mage finish taking notes on the last chapter on The Authoritative Guide to Ironic Transmogrifications or helping an alchemist seal in the last few drops of volatile herbs into a potion.  The light from its bulb will help give energy to those who pass by its beam.', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (11, N'furniture_doormat', N'Welcome Back Doormat', NULL, CAST(0.00 AS Decimal(18, 2)), CAST(500.00 AS Decimal(18, 2)), 1750, N'item_consumeable_covenant_crystal', CAST(30.00 AS Decimal(18, 2)), N'Some might think that it is a humiliation to be transformed into a doormat and trodden all over with muddy shoes.  Luckily the soul inside this one disagrees, arguing that standing guard at the gateway between indoors and out is a great honor (or at least they did back when they had a mouth).  Either way, take a quick glance on the way out.  The doormat may just gift you with a little trinket that will can teleport you back home to friends and family!', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (12, N'furniture_feather_mattress', N'Ultrasoft Feather Mattress', N'effect_Nighty_Night_Judoo', CAST(0.00 AS Decimal(18, 2)), CAST(1500.00 AS Decimal(18, 2)), 1000, NULL, CAST(30.00 AS Decimal(18, 2)), N'While some people may get warm fuzzies knowing that, as a warm piece of clothing, they are keeping their owner warm and happy, some willing inanimates like to go one step farther.  This human has been transformed into a feather mattress that swallows whoever lies down on it in an enchanted warm embrace, lulling them into a long and restful sleep, perfect for the end of a long and hard day.  Unfortunately while this state helps users restore willpower and mana far more quickly than usual, don''t expect these sleep-inducing effects to wear off until after a full eight hours or sleep!', NULL)
INSERT [dbo].[DbStaticFurnitures] ([Id], [dbType], [FriendlyName], [GivesEffect], [APReserveRefillAmount], [BaseCost], [BaseContractTurnLength], [GivesItem], [MinutesUntilReuse], [Description], [PortraitUrl]) VALUES (13, N'furniture_oak_bookshelf', N'Rustic Oak Bookshelf', NULL, CAST(0.00 AS Decimal(18, 2)), CAST(3000.00 AS Decimal(18, 2)), 1000, N'item_consumable_spellbook_large', CAST(120.00 AS Decimal(18, 2)), N'A large, rustic bookshelf.  Inside the stained wood is the soul of a true book-loving mage eager to share some of their magic.  Every now and then a new little spellbook with a few fun spells might just appear on it as if out of thin air!', NULL)
