INSERT [dbo].[DbStaticSkills] ([Id], [dbName], [FriendlyName], [FormdbName], [Description], [ManaCost], [TFPointsAmount], [HealthDamageAmount], [LearnedAtRegion], [LearnedAtLocation], [DiscoveryMessage], [GivesEffect], [ExclusiveToForm], [ExclusiveToItem], [MobilityType], [IsLive], [IsPlayerLearnable]) VALUES (310, N'skill_Blonde_Bimbo_Bombshell_Judoo', N'Blonde Bimbo Bombshell', N'form_Busty_Blonde_Bimbo_Judoo', N'It''s said that blondes have more fun, and the bigger the bust, the better.  If you don''t know one and you''d care to, this spell might take care of that little dilemma.', CAST(7.00 AS Decimal(18, 2)), CAST(10.00 AS Decimal(18, 2)), CAST(4.50 AS Decimal(18, 2)), N'record_store', N'', N'You flip through a box of records. All you see, record after record, are middle aged guys with long greasy hair and stoned expressions on their faces.  Suddenly you flip to a blonde woman whom you recall named her band after her hair color.  She''s rather attractive and you wonder if maybe you two could have hit it off if it weren''t three decades after the album''s release.  Well, she may not be an option, but with all the chaos going on around town as people like you battle it out, you realize you might just be able to create a few more sexy blondes to please your eyes...', NULL, NULL, NULL, N'full', N'live', 1)
